package com.example.securelink

import androidx.compose.ui.test.junit4.createAndroidComposeRule
import androidx.compose.ui.test.onNodeWithText
import androidx.test.ext.junit.runners.AndroidJUnit4
import org.junit.Rule
import org.junit.Test
import org.junit.runner.RunWith
import org.robolectric.annotation.Config

@RunWith(AndroidJUnit4::class)
@Config(sdk = [33])
class MainActivityAdditionalTest {

    @get:Rule
    val composeTestRule = createAndroidComposeRule<MainActivity>()

    @Test
    fun mainActivity_initializesSuccessfully() {
        // Verifica que MainActivity se inicializa correctamente
        // La pantalla de inicio debe estar visible
        composeTestRule.waitForIdle()

        // Verificar que algún elemento de la UI está presente
        composeTestRule.onNodeWithText("Tu primera línea de defensa...").assertExists()
    }

    @Test
    fun mainActivity_showsLogo() {
        composeTestRule.waitForIdle()

        // Verifica que el logo/branding está presente
        composeTestRule.onNodeWithText("Analizamos enlaces...").assertExists()
    }

    @Test
    fun mainActivity_showsNavigationButtons() {
        composeTestRule.waitForIdle()

        // Verifica que los botones de navegación están presentes
        composeTestRule.onNodeWithText("Crea tu Cuenta Gratis").assertExists()
        composeTestRule.onNodeWithText("Inicia sesion").assertExists()
    }

    @Test
    fun mainActivity_showsStatsSection() {
        composeTestRule.waitForIdle()

        // Verifica que la sección de estadísticas está presente
        composeTestRule.onNodeWithText("El Pulso de la Seguridad Digital").assertExists()
    }

    @Test
    fun mainActivity_hasHomeScreenContent() {
        composeTestRule.waitForIdle()

        // Verifica múltiples elementos para asegurar que la pantalla está completamente cargada
        composeTestRule.onNodeWithText("Tu primera línea de defensa...").assertExists()
        composeTestRule.onNodeWithText("Crea tu Cuenta Gratis").assertExists()
        composeTestRule.onNodeWithText("El Pulso de la Seguridad Digital").assertExists()
    }
}

package com.example.securelink.view

import androidx.compose.ui.graphics.Color
import androidx.compose.ui.test.junit4.createComposeRule
import androidx.compose.ui.test.onNodeWithText
import com.example.securelink.model.Report
import com.example.securelink.ui.screens.EstadisticaItem
import com.example.securelink.ui.screens.EstadisticasSeccion
import org.junit.Rule
import org.junit.Test
import org.junit.runner.RunWith
import org.robolectric.RobolectricTestRunner
import org.robolectric.annotation.Config

@RunWith(RobolectricTestRunner::class)
@Config(sdk = [33], manifest = Config.NONE)
class AdditionalComponentsTest {

    @get:Rule
    val composeTestRule = createComposeRule()

    @Test
    fun estadisticaItem_displaysCorrectly() {
        composeTestRule.setContent {
            EstadisticaItem(
                titulo = "Enlaces\nAnalizados",
                valor = "42",
                color = Color.White
            )
        }

        composeTestRule.onNodeWithText("42").assertExists()
        composeTestRule.onNodeWithText("Enlaces\nAnalizados").assertExists()
    }

    @Test
    fun estadisticasSeccion_displaysAllStats() {
        val reportes = listOf(
            Report(id = 1, url = "test1.com", peligro = "seguro", tipoAmenaza = null, createdAt = null, detalles = null, imitaA = null, userId = null),
            Report(id = 2, url = "test2.com", peligro = "peligroso", tipoAmenaza = null, createdAt = null, detalles = null, imitaA = null, userId = null),
            Report(id = 3, url = "test3.com", peligro = "sospechoso", tipoAmenaza = null, createdAt = null, detalles = null, imitaA = null, userId = null),
            Report(id = 4, url = "test4.com", peligro = "seguro", tipoAmenaza = null, createdAt = null, detalles = null, imitaA = null, userId = null)
        )

        composeTestRule.setContent {
            EstadisticasSeccion(
                historialAnalisis = reportes,
                colorFondoOscuro = Color.DarkGray,
                colorSeguro = Color.Green,
                colorPeligroso = Color.Red,
                colorSospechoso = Color.Yellow,
                colorTextoClaro = Color.White
            )
        }

        // Verificar que el componente se renderiza
        composeTestRule.waitForIdle()
    }

    @Test
    fun estadisticasSeccion_withEmptyList() {
        composeTestRule.setContent {
            EstadisticasSeccion(
                historialAnalisis = emptyList(),
                colorFondoOscuro = Color.DarkGray,
                colorSeguro = Color.Green,
                colorPeligroso = Color.Red,
                colorSospechoso = Color.Yellow,
                colorTextoClaro = Color.White
            )
        }

        // Verificar que el componente se renderiza
        composeTestRule.waitForIdle()
    }

    @Test
    fun estadisticasSeccion_withMultipleReportTypes() {
        val reportes = listOf(
            Report(id = 1, url = "test1.com", peligro = "Seguro", tipoAmenaza = null, createdAt = null, detalles = null, imitaA = null, userId = null),
            Report(id = 2, url = "test2.com", peligro = "SEGUROS", tipoAmenaza = null, createdAt = null, detalles = null, imitaA = null, userId = null),
            Report(id = 3, url = "test3.com", peligro = "Peligroso", tipoAmenaza = null, createdAt = null, detalles = null, imitaA = null, userId = null),
            Report(id = 4, url = "test4.com", peligro = "BLOQUEADA", tipoAmenaza = null, createdAt = null, detalles = null, imitaA = null, userId = null),
            Report(id = 5, url = "test5.com", peligro = "Sospechoso", tipoAmenaza = null, createdAt = null, detalles = null, imitaA = null, userId = null),
            Report(id = 6, url = "test6.com", peligro = "SOSPECHOSOS", tipoAmenaza = null, createdAt = null, detalles = null, imitaA = null, userId = null)
        )

        composeTestRule.setContent {
            EstadisticasSeccion(
                historialAnalisis = reportes,
                colorFondoOscuro = Color.DarkGray,
                colorSeguro = Color.Green,
                colorPeligroso = Color.Red,
                colorSospechoso = Color.Yellow,
                colorTextoClaro = Color.White
            )
        }

        // Verificar que el componente se renderiza
        composeTestRule.waitForIdle()
    }

    @Test
    fun reporteCard_withSospechoso_renders() {
        val reporteSospechoso = Report(
            id = 1,
            url = "https://suspicious.test",
            peligro = "sospechoso",
            tipoAmenaza = "Phishing Potencial",
            createdAt = null,
            detalles = "Dominio reciente",
            imitaA = "Google",
            userId = null
        )

        composeTestRule.setContent {
            com.example.securelink.ui.screens.ReporteCard(
                reporte = reporteSospechoso,
                colorFondoOscuro = Color.DarkGray,
                colorSeguro = Color.Green,
                colorPeligroso = Color.Red,
                colorSospechoso = Color.Yellow,
                colorTextoClaro = Color.White
            )
        }

        // Verificar que el componente se renderiza
        composeTestRule.waitForIdle()
    }

    @Test
    fun reporteCard_withNullValues_renders() {
        val reporteNulo = Report(
            id = 1,
            url = "https://unknown.test",
            peligro = null,
            tipoAmenaza = null,
            createdAt = null,
            detalles = null,
            imitaA = null,
            userId = null
        )

        composeTestRule.setContent {
            com.example.securelink.ui.screens.ReporteCard(
                reporte = reporteNulo,
                colorFondoOscuro = Color.DarkGray,
                colorSeguro = Color.Green,
                colorPeligroso = Color.Red,
                colorSospechoso = Color.Yellow,
                colorTextoClaro = Color.White
            )
        }

        // Verificar que el componente se renderiza
        composeTestRule.waitForIdle()
    }

    @Test
    fun reporteCard_withBlockedStatus_renders() {
        val reporteBloqueado = Report(
            id = 1,
            url = "https://blocked.test",
            peligro = "bloqueada",
            tipoAmenaza = "Malware",
            createdAt = null,
            detalles = "Sitio bloqueado por autoridades",
            imitaA = null,
            userId = null
        )

        composeTestRule.setContent {
            com.example.securelink.ui.screens.ReporteCard(
                reporte = reporteBloqueado,
                colorFondoOscuro = Color.DarkGray,
                colorSeguro = Color.Green,
                colorPeligroso = Color.Red,
                colorSospechoso = Color.Yellow,
                colorTextoClaro = Color.White
            )
        }

        // Verificar que el componente se renderiza
        composeTestRule.waitForIdle()
    }
}

package com.example.securelink.view

import androidx.compose.ui.test.junit4.createComposeRule
import androidx.compose.ui.test.onNodeWithText
import com.example.securelink.model.AnalisisResultado
import com.example.securelink.viewmodel.AnalisisEstado
import com.example.securelink.viewmodel.AnalyzerViewModel
import io.mockk.every
import io.mockk.mockk
import kotlinx.coroutines.flow.MutableStateFlow
import org.junit.Rule
import org.junit.Test
import org.junit.runner.RunWith
import org.robolectric.RobolectricTestRunner
import org.robolectric.annotation.Config

@RunWith(RobolectricTestRunner::class)
@Config(sdk = [33], manifest = Config.NONE)
class AnalyzerScreenStatesTest {

    @get:Rule
    val composeTestRule = createComposeRule()

    @Test
    fun analyzerScreen_showsInicial_state() {
        val vm = mockk<AnalyzerViewModel>(relaxed = true)
        val estadoFlow = MutableStateFlow<AnalisisEstado>(AnalisisEstado.Inicial)
        every { vm.estado } returns estadoFlow

        composeTestRule.setContent {
            AnalyzerScreen(analyzerViewModel = vm)
        }

        // Verificar que se muestra el formulario inicial
        composeTestRule.onNodeWithText("Analiza cualquier enlace sospechoso").assertExists()
        composeTestRule.onNodeWithText("Ingresa o Pega Aquí el mensaje o link...").assertExists()
        composeTestRule.onNodeWithText("Escanear Código QR").assertExists()
    }

    @Test
    fun analyzerScreen_showsAnalizando_state() {
        val vm = mockk<AnalyzerViewModel>(relaxed = true)
        val estadoFlow = MutableStateFlow<AnalisisEstado>(AnalisisEstado.Analizando)
        every { vm.estado } returns estadoFlow

        composeTestRule.setContent {
            AnalyzerScreen(analyzerViewModel = vm)
        }

        // Verificar que se muestra el indicador de carga
        composeTestRule.onNodeWithText("Analizando...").assertExists()
    }

    @Test
    fun analyzerScreen_showsResultadoSeguro_state() {
        val vm = mockk<AnalyzerViewModel>(relaxed = true)
        val resultadoSeguro = AnalisisResultado(
            url = "https://safe.com",
            peligro = "seguros",
            tipoAmenaza = null,
            imitaA = null,
            detalles = null
        )
        val estadoFlow = MutableStateFlow<AnalisisEstado>(
            AnalisisEstado.Resultado(listOf(resultadoSeguro))
        )
        every { vm.estado } returns estadoFlow

        composeTestRule.setContent {
            AnalyzerScreen(analyzerViewModel = vm)
        }

        // Verificar que se muestra el resultado seguro
        composeTestRule.onNodeWithText("Enlace Seguro").assertExists()
        composeTestRule.onNodeWithText("No hemos encontrado ninguna amenaza. Puedes proceder con tranquilidad.").assertExists()
    }

    @Test
    fun analyzerScreen_showsResultadoBloqueado_state() {
        val vm = mockk<AnalyzerViewModel>(relaxed = true)
        val resultadoBloqueado = AnalisisResultado(
            url = "https://malicious.com",
            peligro = "bloqueadas",
            tipoAmenaza = "Phishing",
            imitaA = "Banco XYZ",
            detalles = null
        )
        val estadoFlow = MutableStateFlow<AnalisisEstado>(
            AnalisisEstado.Resultado(listOf(resultadoBloqueado))
        )
        every { vm.estado } returns estadoFlow

        composeTestRule.setContent {
            AnalyzerScreen(analyzerViewModel = vm)
        }

        // Verificar que se muestra el resultado bloqueado
        composeTestRule.onNodeWithText("Peligro: Enlace Fraudulento").assertExists()
        composeTestRule.onNodeWithText("Hemos detectado que este sitio es malicioso. Evita interactuar con él.").assertExists()
    }

    @Test
    fun analyzerScreen_showsResultadoSospechoso_state() {
        val vm = mockk<AnalyzerViewModel>(relaxed = true)
        val resultadoSospechoso = AnalisisResultado(
            url = "https://suspicious.com",
            peligro = "sospechosos",
            tipoAmenaza = "Phishing potencial",
            imitaA = "Amazon",
            detalles = null
        )
        val estadoFlow = MutableStateFlow<AnalisisEstado>(
            AnalisisEstado.Resultado(listOf(resultadoSospechoso))
        )
        every { vm.estado } returns estadoFlow

        composeTestRule.setContent {
            AnalyzerScreen(analyzerViewModel = vm)
        }

        // Verificar que se muestra el resultado sospechoso
        composeTestRule.onNodeWithText("Sitio Sospechoso").assertExists()
        composeTestRule.onNodeWithText("Este enlace presenta características inusuales. Te recomendamos no ingresar datos personales.").assertExists()
    }

    @Test
    fun analyzerScreen_showsError_state() {
        val vm = mockk<AnalyzerViewModel>(relaxed = true)
        val estadoFlow = MutableStateFlow<AnalisisEstado>(
            AnalisisEstado.Error("Error de conexión")
        )
        every { vm.estado } returns estadoFlow

        composeTestRule.setContent {
            AnalyzerScreen(analyzerViewModel = vm)
        }

        // Verificar que se muestra el mensaje de error
        composeTestRule.onNodeWithText("Error de conexión").assertExists()
        composeTestRule.onNodeWithText("Reintentar").assertExists()
    }

    @Test
    fun analyzerScreen_showsResultadoWithImitaA() {
        val vm = mockk<AnalyzerViewModel>(relaxed = true)
        val resultado = AnalisisResultado(
            url = "https://fake-bank.com",
            peligro = "bloqueadas",
            tipoAmenaza = "Phishing",
            imitaA = "Banco Nacional",
            detalles = null
        )
        val estadoFlow = MutableStateFlow<AnalisisEstado>(
            AnalisisEstado.Resultado(listOf(resultado))
        )
        every { vm.estado } returns estadoFlow

        composeTestRule.setContent {
            AnalyzerScreen(analyzerViewModel = vm)
        }

        // Verificar que se muestra "Imita a:"
        composeTestRule.onNodeWithText("Imita a: Banco Nacional").assertExists()
    }

    @Test
    fun analyzerScreen_showsResultadoWithURL() {
        val vm = mockk<AnalyzerViewModel>(relaxed = true)
        val resultado = AnalisisResultado(
            url = "https://test.com",
            peligro = "seguros",
            tipoAmenaza = null,
            imitaA = null,
            detalles = null
        )
        val estadoFlow = MutableStateFlow<AnalisisEstado>(
            AnalisisEstado.Resultado(listOf(resultado))
        )
        every { vm.estado } returns estadoFlow

        composeTestRule.setContent {
            AnalyzerScreen(analyzerViewModel = vm)
        }

        // Verificar que se muestra la URL
        composeTestRule.onNodeWithText("URL: https://test.com").assertExists()
    }

    @Test
    fun analyzerScreen_showsAnalyzeAnotherButton() {
        val vm = mockk<AnalyzerViewModel>(relaxed = true)
        val resultado = AnalisisResultado(
            url = "https://test.com",
            peligro = "seguros",
            tipoAmenaza = null,
            imitaA = null,
            detalles = null
        )
        val estadoFlow = MutableStateFlow<AnalisisEstado>(
            AnalisisEstado.Resultado(listOf(resultado))
        )
        every { vm.estado } returns estadoFlow

        composeTestRule.setContent {
            AnalyzerScreen(analyzerViewModel = vm)
        }

        // Verificar que se muestra el botón para analizar otro enlace
        composeTestRule.onNodeWithText("Analizar otro enlace").assertExists()
    }
}

package com.example.securelink.view

import androidx.compose.ui.test.junit4.createComposeRule
import androidx.compose.ui.test.onNodeWithText
import androidx.navigation.NavController
import com.example.securelink.model.LearnItem
import com.example.securelink.model.StatItem
import io.mockk.mockk
import org.junit.Rule
import org.junit.Test
import org.junit.runner.RunWith
import org.robolectric.RobolectricTestRunner
import org.robolectric.annotation.Config

@RunWith(RobolectricTestRunner::class)
@Config(sdk = [33], manifest = Config.NONE)
class HomeScreenAdditionalTest {

    @get:Rule
    val composeTestRule = createComposeRule()

    @Test
    fun homeScreen_withMultipleStats_displaysAll() {
        val navController = mockk<NavController>(relaxed = true)
        val stats = listOf(
            StatItem(count = "100", description = "Enlaces Analizados"),
            StatItem(count = "95", description = "Enlaces Seguros"),
            StatItem(count = "5", description = "Amenazas Bloqueadas")
        )
        val learnItems = listOf(
            LearnItem(
                imageResId = com.example.securelink.R.drawable.ic_launcher_foreground,
                title = "Item 1",
                description = "Desc 1"
            )
        )

        composeTestRule.setContent {
            HomeScreen(stats = stats, navController = navController, learnItems = learnItems)
        }

        // Verificar que todas las estadísticas se muestran
        composeTestRule.onNodeWithText("100").assertExists()
        composeTestRule.onNodeWithText("Enlaces Analizados").assertExists()
        composeTestRule.onNodeWithText("95").assertExists()
        composeTestRule.onNodeWithText("Enlaces Seguros").assertExists()
        composeTestRule.onNodeWithText("5").assertExists()
        composeTestRule.onNodeWithText("Amenazas Bloqueadas").assertExists()
    }

    @Test
    fun homeScreen_withEmptyStats_stillDisplaysUI() {
        val navController = mockk<NavController>(relaxed = true)
        val stats = emptyList<StatItem>()
        val learnItems = emptyList<LearnItem>()

        composeTestRule.setContent {
            HomeScreen(stats = stats, navController = navController, learnItems = learnItems)
        }

        // Verificar que los elementos principales siguen mostrándose
        composeTestRule.onNodeWithText("Tu primera línea de defensa...").assertExists()
        composeTestRule.onNodeWithText("Crea tu Cuenta Gratis").assertExists()
        composeTestRule.onNodeWithText("El Pulso de la Seguridad Digital").assertExists()
    }

    @Test
    fun statsCard_withLargeNumbers_displaysCorrectly() {
        val stat = StatItem(count = "1,234,567", description = "Análisis Totales")

        composeTestRule.setContent {
            StatsCard(stat = stat)
        }

        composeTestRule.onNodeWithText("1,234,567").assertExists()
        composeTestRule.onNodeWithText("Análisis Totales").assertExists()
    }

    @Test
    fun statsSection_withSingleStat_displays() {
        val stats = listOf(
            StatItem(count = "1", description = "Test Único")
        )

        composeTestRule.setContent {
            StatsSection(stats = stats)
        }

        composeTestRule.onNodeWithText("El Pulso de la Seguridad Digital").assertExists()
        composeTestRule.onNodeWithText("1").assertExists()
        composeTestRule.onNodeWithText("Test Único").assertExists()
    }

    @Test
    fun statsSection_withEmptyList_stillShowsTitle() {
        val stats = emptyList<StatItem>()

        composeTestRule.setContent {
            StatsSection(stats = stats)
        }

        composeTestRule.onNodeWithText("El Pulso de la Seguridad Digital").assertExists()
    }

    @Test
    fun homeScreen_allButtonsArePresent() {
        val navController = mockk<NavController>(relaxed = true)
        val stats = listOf(StatItem(count = "10", description = "Tests"))
        val learnItems = listOf(
            LearnItem(
                imageResId = com.example.securelink.R.drawable.ic_launcher_foreground,
                title = "T1",
                description = "D1"
            )
        )

        composeTestRule.setContent {
            HomeScreen(stats = stats, navController = navController, learnItems = learnItems)
        }

        // Verificar ambos botones
        composeTestRule.onNodeWithText("Crea tu Cuenta Gratis").assertExists()
        composeTestRule.onNodeWithText("Inicia sesion").assertExists()
    }

    @Test
    fun homeScreen_displaysCorrectBranding() {
        val navController = mockk<NavController>(relaxed = true)
        val stats = listOf(StatItem(count = "10", description = "Tests"))
        val learnItems = emptyList<LearnItem>()

        composeTestRule.setContent {
            HomeScreen(stats = stats, navController = navController, learnItems = learnItems)
        }

        // Verificar textos de branding
        composeTestRule.onNodeWithText("Tu primera línea de defensa...").assertExists()
        composeTestRule.onNodeWithText("Analizamos enlaces...").assertExists()
    }
}
